import React, { useState, useEffect, useCallback } from 'react';

const ProfileEditModal = ({ isOpen, onClose, section, profile, onSave, loading, userRole }) => {
  const [formData, setFormData] = useState({});

  const initializeFormData = useCallback(() => {
    if (!section || !profile) return;

    switch (section) {
      case 'header':
        setFormData({
          name: profile.name || '',
          location: profile.location || '',
          hourly_rate: profile.hourly_rate || '',
          bio: profile.bio || ''
        });
        break;
      case 'profile_picture':
        setFormData({
          profile_picture: profile.profile_picture || ''
        });
        break;
      case 'skills':
        setFormData({
          skills: profile.skills || []
        });
        break;
      case 'languages':
        setFormData({
          languages: profile.languages || {}
        });
        break;
      case 'availability':
        setFormData({
          availability: profile.availability || ''
        });
        break;
      case 'education':
        setFormData({
          education: profile.education || []
        });
        break;
      case 'certifications':
        setFormData({
          certifications: profile.certifications || []
        });
        break;
      case 'portfolio':
        setFormData({
          portfolio: profile.portfolio || []
        });
        break;
      case 'testimonials':
        setFormData({
          testimonials: profile.testimonials || []
        });
        break;
      case 'employment_history':
        setFormData({
          employment_history: profile.employment_history || []
        });
        break;
      case 'other_experiences':
        setFormData({
          other_experiences: profile.other_experiences || []
        });
        break;
      case 'company_info':
        setFormData({
          company_name: profile.company_name || '',
          company_description: profile.company_description || '',
          company_website: profile.company_website || '',
          company_size: profile.company_size || '',
          industry: profile.industry || ''
        });
        break;
      default:
        setFormData({});
    }
  }, [section, profile]);

  useEffect(() => {
    if (section && profile) {
      initializeFormData();
    }
  }, [section, profile, initializeFormData]);

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  const renderForm = () => {
    switch (section) {
      case 'header':
        return (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Name</label>
              <input
                type="text"
                value={formData.name || ''}
                onChange={(e) => handleInputChange('name', e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Location</label>
              <input
                type="text"
                value={formData.location || ''}
                onChange={(e) => handleInputChange('location', e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              />
            </div>
            {userRole === 'freelancer' && (
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Hourly Rate ($)</label>
                <input
                  type="number"
                  value={formData.hourly_rate || ''}
                  onChange={(e) => handleInputChange('hourly_rate', parseFloat(e.target.value))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                />
              </div>
            )}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Bio</label>
              <textarea
                value={formData.bio || ''}
                onChange={(e) => handleInputChange('bio', e.target.value)}
                rows={4}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              />
            </div>
          </div>
        );

      case 'profile_picture':
        return (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Profile Picture URL</label>
            <input
              type="url"
              value={formData.profile_picture || ''}
              onChange={(e) => handleInputChange('profile_picture', e.target.value)}
              placeholder="https://example.com/image.jpg"
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
            />
          </div>
        );

      case 'skills':
        return (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Skills (comma-separated)</label>
            <input
              type="text"
              value={Array.isArray(formData.skills) ? formData.skills.join(', ') : ''}
              onChange={(e) => handleInputChange('skills', e.target.value.split(',').map(s => s.trim()).filter(s => s))}
              placeholder="Web Development, React, Node.js"
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
            />
          </div>
        );

      case 'languages':
        return (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Language</label>
              <input
                type="text"
                placeholder="English"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Proficiency Level</label>
              <select className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500">
                <option>Conversational</option>
                <option>Fluent</option>
                <option>Native</option>
              </select>
            </div>
          </div>
        );

      case 'availability':
        return (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Availability</label>
            <select
              value={formData.availability || ''}
              onChange={(e) => handleInputChange('availability', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
            >
              <option value="">Select availability</option>
              <option value="Less than 10 hrs/week">Less than 10 hrs/week</option>
              <option value="10-19 hrs/week">10-19 hrs/week</option>
              <option value="20-29 hrs/week">20-29 hrs/week</option>
              <option value="30-39 hrs/week">30-39 hrs/week</option>
              <option value="More than 30 hrs/week">More than 30 hrs/week</option>
            </select>
          </div>
        );

      case 'company_info':
        return (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Company Name</label>
              <input
                type="text"
                value={formData.company_name || ''}
                onChange={(e) => handleInputChange('company_name', e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Company Description</label>
              <textarea
                value={formData.company_description || ''}
                onChange={(e) => handleInputChange('company_description', e.target.value)}
                rows={4}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Company Website</label>
              <input
                type="url"
                value={formData.company_website || ''}
                onChange={(e) => handleInputChange('company_website', e.target.value)}
                placeholder="https://example.com"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Company Size</label>
              <select
                value={formData.company_size || ''}
                onChange={(e) => handleInputChange('company_size', e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              >
                <option value="">Select company size</option>
                <option value="1-10 employees">1-10 employees</option>
                <option value="11-50 employees">11-50 employees</option>
                <option value="51-200 employees">51-200 employees</option>
                <option value="201-500 employees">201-500 employees</option>
                <option value="500+ employees">500+ employees</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Industry</label>
              <input
                type="text"
                value={formData.industry || ''}
                onChange={(e) => handleInputChange('industry', e.target.value)}
                placeholder="Technology, Healthcare, Finance, etc."
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              />
            </div>
          </div>
        );

      default:
        return (
          <div className="text-center py-8">
            <p className="text-gray-500">Edit form for {section} not implemented yet</p>
          </div>
        );
    }
  };

  const getModalTitle = () => {
    const titles = {
      header: 'Edit Profile',
      profile_picture: 'Update Profile Picture',
      skills: 'Edit Skills',
      languages: 'Edit Languages',
      availability: 'Edit Availability',
      education: 'Edit Education',
      certifications: 'Edit Certifications',
      portfolio: 'Edit Portfolio',
      testimonials: 'Edit Testimonials',
      employment_history: 'Edit Employment History',
      other_experiences: 'Edit Other Experiences',
      company_info: 'Edit Company Information'
    };
    return titles[section] || 'Edit Profile';
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
        <div className="mt-3">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-medium text-gray-900">{getModalTitle()}</h3>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            {renderForm()}

            <div className="flex justify-end space-x-3 pt-4">
              <button
                type="button"
                onClick={onClose}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={loading}
                className="px-4 py-2 text-sm font-medium text-white bg-green-600 border border-transparent rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 disabled:opacity-50"
              >
                {loading ? 'Saving...' : 'Save Changes'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ProfileEditModal;
